import React from "react";
import { Route, Switch } from "react-router-dom";
import ItemCard from "./ItemCard";
import ShowPage from "./ShowPage";
import "../../Styling/ItemContainer/ItemContainer.css";

class ItemContainer extends React.Component {
  state = {
    items: []
  };

  fetchItems = () => {
    console.log("ITEMCONTTTT items Fetch starting");
    fetch("http://localhost:3000/items")
      .then(res => res.json())
      .then(items => {
        this.setState(
          { items: items },
          console.log("these are my items", items)
        );
      });
  };

  render() {
    console.log("this is my user in ItemCont:", this.props.user);
    let items = this.state.items.map(item => (
      <ItemCard key={item.id} item={item} />
    ));

    return (
      <div>
        <Switch>
          <Route
            path="/items/:id"
            render={routerProps => {
              let id = parseInt(routerProps.match.params.id);
              // console.log("items are:", this.props.items)
              let item = this.props.items.find(item => item.id === id);
              return <ShowPage item={item} />;
            }}
          />
          <Route
            path="/items"
            render={() => {
              return <div className="item-collection">{items}</div>;
            }}
          />
        </Switch>
      </div>
    );
  }
}

export default ItemContainer;
