import React from "react";
import { Route, Switch, withRouter } from "react-router-dom";
import ItemCard from "./ItemCard";
import ShowPage from "./ShowPage/ShowPage";
import "../../Styling/ItemContainer/ItemContainer.css";
import SearchBar from "./SearchBar";

class ItemContainer extends React.Component {
  render() {
    console.log("testing123", this.props);
    let items = this.props.items.map(item => (
      <ItemCard key={item.id} item={item} user={this.props.user} />
    ));
    console.log("this is my user in ItemCont:", this.props.user);

    return (
      <div>
        <Switch>
          <Route
            path="/main/items/:id"
            render={routerProps => {
              let id = parseInt(routerProps.match.params.id);
              console.log("items are:", this.props.items);
              let item = this.props.items.find(item => item.id === id);
              return <ShowPage item={item} user={this.props.user} />;
            }}
          />
          <Route
            path="/main/items"
            render={routerProps => {
              return (
                <div>
                  <SearchBar
                    onChangeHandler={this.props.onChangeHandler}
                    searchTerm={this.props.searchTerm}
                  />
                  <div className="item-collection">{items}</div>
                </div>
              );
            }}
          />
        </Switch>
      </div>
    );
  }
}

export default ItemContainer;
