    //   <UserPurchases items={myRecipes} />
    // </div>

    return (
      <div>
        <div className="user-view">
          <div />
          <div className="user-left-side">
            <h2 className="favorites"> My Favorite Items </h2>
            <FavItems items={this.favoriteItems()} />;
          </div>
        </div>
      </div>
    );
  }
}

export default Favorites;
