import React from "react";
import { Link } from "react-router-dom";
import { Card, Button } from "react-bootstrap";
import { capitalize } from "../../Utilities/Utilities";
// import "../../Styling/ItemContainer/Item.css";
import "../../Styling/ItemContainer/ItemCard.css";
import "../../Styling/ItemContainer/ItemSFX.css";

// FOCUS WORK HERE, THIS IS WHERE WE HANDLE WHERE WE COME FROM TO EXECUTE DIFFERENT COMMANDS
class ItemCard extends React.Component {
  render() {
    let { name, category, price, image_url, description } = this.props.item;

    return (
      <React.Fragment>
        <div className="container">
          <div className="row">
            <div className="product-grid2">
              <div className="product-image2">
                <a href="#">
                  <img className="pic-1" src={image_url} />
                  <img
                    className="pic-2"
                    src="https://cdn.shopify.com/s/files/1/1071/7482/t/10/assets/logo.png?15"
                  />
                </a>
                <ul class="social">
                  <li>
                    <a href="#" data-tip="Quick View">
                      <i class="fa fa-eye" />
                    </a>
                  </li>
                  <li>
                    <a href="#" data-tip="Add to Wishlist">
                      <i class="fa fa-shopping-bag" />
                    </a>
                  </li>
                  <li>
                    <a href="#" data-tip="Add to Cart">
                      <i class="fa fa-shopping-cart" />
                    </a>
                  </li>
                </ul>
                <a class="add-to-fav" href="">
                  Add to Favs
                </a>
              </div>
              <h3>{name}</h3>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default ItemCard;
