import React from "react";
import { Link } from "react-router-dom";
import { Card, Button } from "react-bootstrap";
import { capitalize } from "../../Utilities/Utilities";

import "../../Styling/ItemContainer/ItemCard.css";
import "../../Styling/ItemContainer/ItemSFX.css";

// FOCUS WORK HERE, THIS IS WHERE WE HANDLE WHERE WE COME FROM TO EXECUTE DIFFERENT COMMANDS
class ItemCard extends React.Component {
  render() {
    let {
      name,
      category,
      price,
      image_url,
      description,
      sold_by
    } = this.props.item;

    return (
      <React.Fragment>
        <div className="container">
          <div className="row">
            <div className="product-grid2">
              <div className="product-image2">
                <a href={`/main/items/${this.props.item.id}`}>
                  <img className="pic-1" src={image_url} />
                  <img
                    className="pic-2"
                    src="https://cdn.shopify.com/s/files/1/1071/7482/t/10/assets/logo.png?15"
                  />
                </a>
                <ul className="social">
                  <li>
                    <a href="#" data-tip="Quick View">
                      <i className="fa fa-eye" />
                    </a>
                  </li>
                </ul>
                <a className="add-to-fav" href="">
                  Add to Favs
                </a>
              </div>
              <div className="product-content">
                <h3 className="title">
                  <a href={`/main/items/${this.props.item.id}`}>{name}</a>
                </h3>
                <span className="price">
                  ${price}
                  {sold_by}
                </span>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default ItemCard;
