import React from "react";
import { Navbar, Nav, Button } from "react-bootstrap";
import { Container, Col, Form, FormGroup, Label, Input } from "reactstrap";
import Jumbotron from "react-bootstrap/Jumbotron";
import logo from "../../Images/star.png";

class Signup extends React.Component {
  state = {
    username: "",
    password: "",
    email: "",
    phoneNumber: ""
  };

  onChangeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onCreateUserHandler = () => {
    this.props.onCreateUserHandler(this.state);
  };

  render() {
    let logInStyle = {
      width: "800px",
      margin: "0 auto"
    };

    let inputStyle = {
      width: "400px",
      borderWidth: "normal",
      borderColor: "black",
      margin: "0 auto"
    };

    return (
      <div style={logInStyle}>
        <Jumbotron>
          <h1 className={`fas fa-${this.props.logo}`}>Wunder-Signup</h1>
          <p>
            The best way to sell all your goods online. Pickup in person or
            delivery.
          </p>
          <p>
            <Button className="mr-2" variant="primary" href="/login">
              LogIn
            </Button>
            <Button className="mr-2" variant="success" href="/main">
              Main Menu
            </Button>
          </p>

          <h3 className="App"> Welcome to Wunder! Signup to get started! </h3>
          <Form className="form">
            <Col>
              <FormGroup>
                <Label>Username</Label>
                <Input
                  onChange={this.onChangeHandler}
                  type="text"
                  name="username"
                  placeholder="My Username"
                  style={inputStyle}
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="examplePassword">Password</Label>
                <Input
                  onChange={this.onChangeHandler}
                  type="password"
                  name="password"
                  placeholder="Password"
                  style={inputStyle}
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="email">Email</Label>
                <Input
                  onChange={this.onChangeHandler}
                  type="text"
                  name="email"
                  placeholder="Email"
                  style={inputStyle}
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="phoneNumber">Phone Number</Label>
                <Input
                  onChange={this.onChangeHandler}
                  type="number"
                  name="phoneNumber"
                  placeholder="Phone Number"
                  style={inputStyle}
                />
              </FormGroup>
            </Col>
            <Button variant="primary" onClick={this.onCreateUserHandler}>
              Create Account
            </Button>
          </Form>
        </Jumbotron>
      </div>
    );
  }
}

export default Signup;
