import React from "react";

// import ItemContainer from "./ItemContainer";
import { Route, Switch } from "react-router";
import { withRouter } from "react-router-dom";
import MainMenu from "./MainMenu";
import ItemContainer from "../ItemContainer/ItemContainer";
import LandingPage from "./Components/LandingPage/LandingPage";
import Signup from "./Components//LogIn-SignUp/SignUp";
import Login from "./Components//LogIn-SignUp/LogIn";

class Main extends React.Component {
  state = {
    items: [],
    user: null
  };

  // ================= Fetch Data =====================
  componentDidMount() {
    console.log("Main did mount");
    this.setState({ user: this.props.user });
    this.fetchItems();
    // this.fetchCategories();
  }

  // ----------------- Fetch Items ---------------------
  fetchItems = () => {
    console.log("items Fetch starting");
    fetch("http://localhost:3000/items")
      .then(res => res.json())
      .then(items => {
        this.setState(
          { items: items },
          console.log("these are my items", items)
        );
      });
  };

  render() {
    let items = this.state.items;
    console.log("this is my user in Main:", this.props.user);

    return (
      <div>
        <Switch>
          <Route
            path="/items"
            render={routerProps => <ItemContainer items={items} />}
          />
          <Route
            path="/main"
            render={routerProps => (
              <div>
                <Main
                  logOutHandler={this.logoutHandler}
                  user={this.state.user}
                />
              </div>
            )}
          />
          <Route
            path="/login"
            render={routerProps => (
              <div>
                <Login
                  onLoginHandler={this.onLoginHandler}
                  title="Wunder"
                  color="primary"
                />
              </div>
            )}
          />
          <Route
            path="/signup"
            render={routerProps => (
              <div>
                <Signup
                  onCreateUserHandler={this.onCreateUserHandler}
                  title="Wunder"
                  color="primary"
                />
              </div>
            )}
          />
          {/* ------ Any other route put above this line ------ */}
          <Route
            path="/"
            render={routerProps => <LandingPage title="Wunder" />}
          />
        </Switch>
      </div>
    );
  }
}

export default withRouter(Main);
