import React from "react";
import { ActionCableConsumer } from "react-actioncable-provider";
import { API_ROOT } from "../../Constants";
import NewChatForm from "./NewChatForm";
import MessagesArea from "./MessagesArea";
import Cable from "./Cable";

class ChatsList extends React.Component {
  state = {
    chats: [],
    activeChat: null,
    user: this.props.user
  };

  componentDidMount = () => {
    fetch(`${API_ROOT}/chats`)
      .then(res => res.json())
      .then(chats => this.setState({ chats }));
  };

  handleClick = id => {
    console.log("user????", this.state.user);
    this.setState({ activeChat: id, user: this.state.user });
  };

  handleReceivedChat = response => {
    const { chat } = response;
    this.setState({
      chats: [...this.state.chats, chat]
    });
  };

  handleReceivedMessage = response => {
    const { message } = response;
    const chats = [...this.state.chats];
    const chat = chats.find(chat => chat.id === message.chat_id);
    chat.messages = [...chat.messages, message];
    this.setState({ chats });
  };

  render = () => {
    const { chats, activeChat, user } = this.state;
    console.log("oin chats list USER:", this.props.user);
    // -------------------so weird that line 43 classname and line 55 chats if same name then double results-----
    return (
      <div className="chattybox">
        <ActionCableConsumer
          channel={{ channel: "ChatsChannel" }}
          onReceived={this.handleReceivedChat}
        />
        {this.state.chats.length ? (
          <Cable
            chats={chats}
            handleReceivedMessage={this.handleReceivedMessage}
          />
        ) : null}
        <h2>Chats</h2>
        <ul>{mapChats(chats, this.handleClick, user)}</ul>
        <NewChatForm user={this.props.user} />
        {activeChat && user ? (
          <MessagesArea user={user} chat={findActiveChat(chats, activeChat)} />
        ) : null}
      </div>
    );
  };
}

export default ChatsList;

// helpers

const findActiveChat = (chats, activeChat) => {
  return chats.find(chat => chat.id === activeChat);
};

const mapChats = (chats, handleClick, user) => {
  return chats.map(chat => {
    return (
      <li key={chat.id} onClick={() => handleClick(chat.id, user)}>
        {chat.title}
      </li>
    );
  });
};
